mapper.update:
UPDATE INSA SET NAME = #{name}, REG_NO = #{reg_no}, HP = #{hp}, PWD = #{pwd}, ENG_NAME = #{eng_name}, JOIN_YN = #{join_yn}, PHONE = #{phone}, AGE = #{age}, EMAIL = #{email}, JOIN_GBN_CODE = #{join_gbn_code}, SALARY = #{salary}, GART_LEVEL = #{gart_level}, PUT_YN = #{put_yn}, MIL_YN = #{mil_yn}, MIL_TYPE = #{mil_type, jdbcType=VARCHAR}, MIL_LEVEL = #{mil_level, jdbcType=VARCHAR}, MIL_STARTDATE = #{mil_startdate, jdbcType=VARCHAR}, MIL_ENDDATE = #{mil_enddate, jdbcType=VARCHAR}, KOSA_REG_YN = #{kosa_reg_yn}, KOSA_CLASS_CODE = #{kosa_class_code, jdbcType=VARCHAR}, JOIN_DAY = #{join_day}, RETIRE_DAY = #{retire_day}, CMP_REG_NO = #{cmp_reg_no}, CRM_NAME = #{crm_name}, SELF_INTRO = #{self_intro}
, PROFILE_IMAGE = #{profile_image}
, CARRIER_IMAGE = #{carrier_image}
, CMP_REG_IMAGE = #{cmp_reg_image}
WHERE SABUN = #{sabun}

mapper.select:

SELECT COUNT(*)	FROM INSA
WHERE 1 = 1


	AND SABUN LIKE '%'||#{sabun}||'%'


	AND NAME LIKE '%'||#{name}||'%'


	AND JOIN_YN = #{join_yn}							


	AND PUT_YN = #{put_yn}


	AND POS_GBN_CODE = #{pos_gbn_code}


	AND TO_CHAR(JOIN_DAY, 'YYYY-MM-DD') LIKE '%'||#{join_day}||'%'


	AND TO_CHAR(RETIRE_DAY, 'YYYY-MM-DD') LIKE '%'||#{retire_day}||'%'


	AND JOIN_GBN_CODE = #{join_gbn_code}


ORDER BY SABUN

SELECT ROWNUM, P.* FROM (SELECT ROWNUM AS RNUM, K.* FROM (SELECT SABUN, NAME, REG_NO, HP, POS_GBN_CODE, JOIN_DAY, RETIRE_DAY, PUT_YN, SALARY FROM INSA WHERE 1 = 1
AND SABUN LIKE '%'||#{sabun}||'%'
AND NAME LIKE '%'||#{name}||'%'
AND JOIN_YN = #{join_yn}
AND PUT_YN = #{put_yn}
AND POS_GBN_CODE = #{pos_gbn_code}
AND TO_CHAR(JOIN_DAY, 'YYYY-MM-DD') LIKE '%'||#{join_day}||'%'
AND TO_CHAR(RETIRE_DAY, 'YYYY-MM-DD') LIKE '%'||#{retire_day}||'%'
AND JOIN_GBN_CODE = #{join_gbn_code}
ORDER BY SABUN) K WHERE ROWNUM <= #{endRow}) P WHERE RNUM >= #{startRow}
