mapper.select:

SELECT SABUN, NAME, REG_NO, HP,
	POS_GBN_CODE, JOIN_DAY, RETIRE_DAY,
	PUT_YN, SALARY
FROM INSA
WHERE 1 = 1


	AND SABUN LIKE '%'||#{sabun}||'%'


	AND NAME LIKE '%'||#{name}||'%'


	AND JOIN_YN = #{join_yn}


	AND PUT_YN = #{put_yn}


	AND POS_GBN_CODE = #{pos_gbn_code}


	AND JOIN_DAY = #{join_day}


	AND RETIRE_DAY = #{retire_day}


	AND JOIN_GBN_CODE = #{join_gbn_code}


ORDER BY SABUN
